        if (isFn || isArrFn) {
          if (fn.body.type === 'BlockStatement') {
            var returnStatement = getReturnStatement(fn.body.body);
            if (returnStatement && returnStatement.argument) {
              checkIteratorElement(returnStatement.argument);
            }
          }
        }
      }
    };
  }
};

